// Helper to find a theme object by its ID across all categories
const getThemeById = (id: number): ThemeItem | undefined => {
  for (const cat of TOPIC_DATA) {
    const item = cat.items.find(i => i.id === id);
    if (item) return item;
  }
  return undefined;
};

